import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/style.css';
import UserContext from './UserContext';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { usePosition } from 'use-position';
import useGlobalHook from 'use-global-hook';

const initialState = {
    gender: 0,
    age: 0
};

const actions = {
    modifyState: (newGender, newAge) => {
        store.setState({ 
            gender: newGender,
            age: newAge
        });
    },
};

const useGlobal = useGlobalHook(React, initialState, actions);

const Index = () => {
    const { latitude, longitude, timestamp, accuracy, error } = usePosition();
    const [globalState, globalActions] = useGlobal();

    const handleSubmit = event => {
        const form = event.target;
        globalActions.modifyState(form.elements.gender.value, form.elements.age.value);
    };

    return (
        <div className='container'>
            <div>
                <h1>Welcome!</h1>
                <Form onSubmit={handleSubmit}>
                    <Form.Group controlId="exampleForm.ControlSelect1">
                        <Form.Label>Gender</Form.Label>
                        <Form.Control name='gender' as="select" required>
                            <option>Male</option>
                            <option>Female</option>
                            <option>Other</option>
                        </Form.Control>
                    </Form.Group>
                    <Form.Group controlId="exampleForm.ControlSelect2">
                        <Form.Label>Age</Form.Label>
                        <Form.Control name='age'/>
                    </Form.Group>
                    <div className='buttonContainer'>
                        <Button variant="info" type="submit" size="lg">
                            Submit
                        </Button>
                    </div>        
                </Form>
            </div>
        </div>
    );
}

export default Index;


